import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './TaskPage.css';

const TaskPage = () => {
  const [tasks, setTasks] = useState([]);
  const [search, setSearch] = useState('');
  const [sortBy, setSortBy] = useState('recent');
  const navigate = useNavigate();

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/tasks', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
      toast.error('Failed to fetch tasks');
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/tasks/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setTasks((prevTasks) => prevTasks.filter((task) => task.id !== id));
      toast.success('Task deleted successfully!');
    } catch (error) {
      console.error('Error deleting task:', error);
      toast.error('Failed to delete task');
    }
  };

  const handleEdit = (id) => {
    navigate(`/edit-task/${id}`);
  };

  const handleViewDetails = (id) => {
    navigate(`/view-task/${id}`);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const handleAddTask = () => {
    navigate('/add-task');
  };

  const handleDragEnd = async (result) => {
    if (!result.destination) return;

    const { source, destination } = result;

    if (source.droppableId !== destination.droppableId) {
      const updatedTasks = tasks.map((task) => {
        if (task.id === result.draggableId) {
          task.status = destination.droppableId;
        }
        return task;
      });

      setTasks(updatedTasks);

      try {
        const token = localStorage.getItem('token');
        await axios.put(
          `http://localhost:5000/api/tasks/${result.draggableId}`,
          { status: destination.droppableId },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        toast.success('Task status updated!');
      } catch (error) {
        console.error('Error updating task status:', error);
        toast.error('Failed to update task status');
      }
    }
  };

  const filteredTasks = tasks.filter(
    (task) =>
      task.title.toLowerCase().includes(search.toLowerCase()) ||
      task.description.toLowerCase().includes(search.toLowerCase())
  );

  const sortedTasks = filteredTasks.sort((a, b) => {
    if (sortBy === 'recent') {
      return new Date(b.createdAt) - new Date(a.createdAt);
    }
    // Add other sort conditions here
    return 0;
  });

  const categorizeTasks = (status) =>
    sortedTasks.filter((task) => task.status === status);

  return (
    <div className="task-page">
      <header className="header">
        <div className="avatar">
          <img src="path_to_avatar_image" alt="User Avatar" className="avatar-image" />
        </div>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </header>
      <div className="add-task-container">
        <button className="add-task-button" onClick={handleAddTask}>
          Add Task
        </button>
      </div>
      <div className="search-sort">
        <input
          type="text"
          placeholder="Search..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="recent">Recent</option>
          {/* Add more sorting options if needed */}
        </select>
      </div>
      <DragDropContext onDragEnd={handleDragEnd}>
        <div className="task-columns">
          {['TODO', 'IN_PROGRESS', 'DONE'].map((status) => (
            <Droppable droppableId={status} key={status}>
              {(provided) => (
                <div
                  className="task-column"
                  {...provided.droppableProps}
                  ref={provided.innerRef}
                >
                  <h2>{status.replace('_', ' ')}</h2>
                  {categorizeTasks(status).map((task, index) => (
                    <Draggable
                      key={task.id}
                      draggableId={task.id.toString()}
                      index={index}
                    >
                      {(provided) => (
                        <div
                          className="task-card"
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                        >
                          <h3>{task.title}</h3>
                          <p>{task.description}</p>
                          <p>
                            Created at: {new Date(task.createdAt).toLocaleString()}
                          </p>
                          <div className="task-actions">
                            <button
                              className="delete-button"
                              onClick={() => handleDelete(task.id)}
                            >
                              Delete
                            </button>
                            <button onClick={() => handleEdit(task.id)}>Edit</button>
                            <button onClick={() => handleViewDetails(task.id)}>
                              View Details
                            </button>
                          </div>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>
      <ToastContainer />
    </div>
  );
};

export default TaskPage;
