import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import './TaskPage.css';

// TaskCard component to handle dragging
const TaskCard = ({ task }) => {
  const [{ isDragging }, dragRef] = useDrag({
    type: 'TASK',
    item: { id: task.id, status: task.status },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  return (
    <div
      ref={dragRef}
      className="task-card"
      style={{ opacity: isDragging ? 0.5 : 1 }}
    >
      <h3>{task.title}</h3>
      <p>{task.description}</p>
    </div>
  );
};

// TaskColumn component to handle dropping
const TaskColumn = ({ status, tasks, moveTask }) => {
  const [{ isOver }, dropRef] = useDrop({
    accept: 'TASK',
    drop: (item) => moveTask(item.id, status),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  });

  return (
    <div
      ref={dropRef}
      className="task-column"
      style={{ backgroundColor: isOver ? '#f0f0f0' : 'white' }}
    >
      <h2>{status.replace('_', ' ')}</h2>
      {tasks.map((task) => (
        <TaskCard key={task.id} task={task} />
      ))}
    </div>
  );
};

const TaskPage = () => {
  const [tasks, setTasks] = useState([]);
  const [search, setSearch] = useState('');
  const [sortBy, setSortBy] = useState('recent');
  const navigate = useNavigate();

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      toast.info('You are logged in as a guest. Tasks cannot be fetched.');
      return;
    }

    try {
      const response = await axios.get('http://localhost:5000/api/tasks', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
      toast.error('Failed to fetch tasks');
    }
  };

  const moveTask = async (taskId, newStatus) => {
    try {
      const updatedTasks = tasks.map((task) =>
        task.id === taskId ? { ...task, status: newStatus } : task
      );
      setTasks(updatedTasks);

      const token = localStorage.getItem('token');
      await axios.put(
        `http://localhost:5000/api/tasks/${taskId}`,
        { status: newStatus },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      toast.success('Task status updated successfully!');
    } catch (error) {
      console.error('Error updating task status:', error);
      toast.error('Failed to update task status');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const handleAddTask = () => {
    navigate('/add-task');
  };

  const filteredTasks = tasks.filter(
    (task) =>
      task.title.toLowerCase().includes(search.toLowerCase()) ||
      task.description.toLowerCase().includes(search.toLowerCase())
  );

  const sortedTasks = filteredTasks.sort((a, b) => {
    switch (sortBy) {
      case 'recent':
        return new Date(b.createdAt) - new Date(a.createdAt);
      case 'lastUpdated':
        return new Date(b.updatedAt) - new Date(a.updatedAt);
      case 'alphabetical':
        return a.title.localeCompare(b.title);
      case 'status':
        return a.status.localeCompare(b.status);
      default:
        return 0;
    }
  });

  const categorizeTasks = (status) =>
    sortedTasks.filter((task) => task.status === status);

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="task-page">
        <header className="header">
          <div className="avatar">
            <img src="path_to_avatar_image" alt="User Avatar" className="avatar-image" />
          </div>
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>
        </header>

        <div className="add-task-container">
          <button className="add-task-button" onClick={handleAddTask}>
            Add Task
          </button>
        </div>

        <div className="search-sort">
          <input
            type="text"
            placeholder="Search..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
            <option value="recent">Sort by Creation Date</option>
            <option value="lastUpdated">Sort by Last Updated</option>
            <option value="alphabetical">Sort by Alphabetical Order</option>
            <option value="status">Sort by Status</option>
          </select>
        </div>

        <div className="task-columns">
          {['TODO', 'IN_PROGRESS', 'DONE'].map((status) => (
            <TaskColumn
              key={status}
              status={status}
              tasks={categorizeTasks(status)}
              moveTask={moveTask}
            />
          ))}
        </div>

        <ToastContainer />
      </div>
    </DndProvider>
  );
};

export default TaskPage;
