import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './TaskPage.css';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const TaskPage = () => {
  const [tasks, setTasks] = useState([]);
  const [search, setSearch] = useState('');
  const [sortBy, setSortBy] = useState('recent');
  const navigate = useNavigate();

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    const token = localStorage.getItem('token');
    
    if (!token) {
      toast.info('You are logged in as a guest. Tasks cannot be fetched.');
      return;
    }

    try {
      const response = await axios.get('http://localhost:5000/api/tasks', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
      toast.error('Failed to fetch tasks');
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/tasks/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setTasks((prevTasks) => prevTasks.filter((task) => task._id !== id));
      toast.success('Task deleted successfully!');
    } catch (error) {
      console.error('Error deleting task:', error);
      toast.error('Failed to delete task');
    }
  };

  const handleEdit = (id) => {
    navigate(`/edit-task/${id}`);
  };

  const handleViewDetails = (id) => {
    navigate(`/view-task/${id}`);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const handleAddTask = () => {
    navigate('/add-task');
  };

  const onDragEnd = async (result) => {
    const { destination, source, draggableId } = result;

    if (!destination) return;

    if (destination.droppableId === source.droppableId && destination.index === source.index) return;

    const updatedTasks = Array.from(tasks);
    const [movedTask] = updatedTasks.splice(source.index, 1);
    movedTask.status = destination.droppableId;
    updatedTasks.splice(destination.index, 0, movedTask);

    setTasks(updatedTasks);

    try {
      await axios.put(`http://localhost:5000/api/tasks/${movedTask._id}`, { status: movedTask.status }, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
    } catch (error) {
      console.error('Error updating task status:', error);
      toast.error('Failed to update task status');
    }
  };

  const filteredTasks = tasks.filter(
    (task) =>
      task.title.toLowerCase().includes(search.toLowerCase()) ||
      task.description.toLowerCase().includes(search.toLowerCase())
  );

  const sortedTasks = filteredTasks.sort((a, b) => {
    switch (sortBy) {
      case 'recent':
        return new Date(b.createdAt) - new Date(a.createdAt);
      case 'lastUpdated':
        return new Date(b.updatedAt) - new Date(a.updatedAt);
      case 'alphabetical':
        return a.title.localeCompare(b.title);
      case 'status':
        return a.status.localeCompare(b.status);
      default:
        return 0;
    }
  });

  const categorizeTasks = (status) =>
    sortedTasks.filter((task) => task.status === status);

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="task-page">
        <header className="header">
          <div className="avatar">
            <img src="path_to_avatar_image" alt="User Avatar" className="avatar-image" />
          </div>
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>
        </header>
        <div className="add-task-container">
          <button className="add-task-button" onClick={handleAddTask}>
            Add Task
          </button>
        </div>
        <div className="search-sort">
          <input
            type="text"
            placeholder="Search..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
            <option value="recent">Sort by Creation Date</option>
            <option value="lastUpdated">Sort by Last Updated</option>
            <option value="alphabetical">Sort by Alphabetical Order</option>
            <option value="status">Sort by Status</option>
          </select>
        </div>
        <div className="task-columns">
          {['TODO', 'IN_PROGRESS', 'DONE'].map((status) => (
            <Droppable key={status} droppableId={status}>
              {(provided) => (
                <div
                  className="task-column"
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                >
                  <h2>{status.replace('_', ' ')}</h2>
                  {categorizeTasks(status).map((task, index) => (
                    <Draggable key={task._id} draggableId={task._id} index={index}>
                      {(provided) => (
                        <div
                          className="task-card"
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                        >
                          <h3>{task.title}</h3>
                          <p>{task.description}</p>
                          <p>Created at: {new Date(task.createdAt).toLocaleString()}</p>
                          <p>Last updated: {new Date(task.updatedAt).toLocaleString()}</p>
                          <div className="task-actions">
                            <button className="delete-button" onClick={() => handleDelete(task._id)}>Delete</button>
                            <button onClick={() => handleEdit(task._id)}>Edit</button>
                            <button onClick={() => handleViewDetails(task._id)}>View Details</button>
                          </div>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
        <ToastContainer />
      </div>
    </DragDropContext>
  );
};

export default TaskPage;
